import React, { useEffect, useState } from "react";
import { Button } from "react-bootstrap";
import Table from "react-bootstrap/esm/Table";
import axios from "axios";
import AddData from "../AddData/AddData";
import { Link, useNavigate } from "react-router-dom";

const ListData = () => {
  const [data, setData] = useState([]);
  const navigate = useNavigate();
  const [show, setShow] = useState(false);

  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    const response = await axios.get("http://localhost:5000/mahasiswa");
    setData(response.data);
  };

  const deleteData = async (id) => {
    try {
      await axios.delete("http://localhost:5000/mahasiswa/" + id);
      getData();
    } catch (err) {
      console.log(err);
    }
  };

  const getDataById = async (id) => {
    try {
      const response = await axios.post(
        `http://localhost:5000/mahasiswa/${id}`
      );
      setDataEdit({
        nim: response.data.nim,
        nama: response.data.nama,
      });
    } catch (err) {
      console.log(err);
    }
    setShow(true);
  };

  return (
    <React.Fragment>
      <div className="d-flex justify-content-start mb-3">
        <Link to="/add-data">
          <Button variant="success" className="me-3">
            Add Data
          </Button>
        </Link>
      </div>
      <Table striped bordered hover size="sm">
        <thead>
          <tr>
            <th>#</th>
            <th>NIM</th>
            <th>Nama</th>
            <th style={{ width: 250 }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => {
            const i = index;
            return (
              <React.Fragment>
                <tr>
                  <td>{index + 1}</td>
                  <td>{item.nim}</td>
                  <td>{item.nama}</td>
                  <td>
                    {/* <Button
                    variant="primary"
                    className="me-3"
                    onClick={() => navigate(`/edit-data/${item.id}`)}
                  >
                    Edit
                  </Button> */}
                    <Button
                      variant="primary"
                      className="me-3"
                      onClick={() => getDataById(item.id)}
                    >
                      Edit
                    </Button>
                    <Button
                      variant="danger"
                      onClick={() => deleteData(item.id)}
                    >
                      Delete
                    </Button>
                  </td>
                </tr>

                <Modal show={show} onHide={handleClose}>
                  <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                  </Modal.Header>
                  <Modal.Body>
                    <Form>
                      <Form.Group
                        className="mb-3"
                        controlId="exampleForm.ControlInput1"
                      >
                        <Form.Label>Email address</Form.Label>
                        <Form.Control
                          type="email"
                          placeholder="name@example.com"
                          autoFocus
                        />
                      </Form.Group>
                      <Form.Group
                        className="mb-3"
                        controlId="exampleForm.ControlTextarea1"
                      >
                        <Form.Label>Example textarea</Form.Label>
                        <Form.Control as="textarea" rows={3} />
                      </Form.Group>
                    </Form>
                  </Modal.Body>
                  <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                      Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                      Save Changes
                    </Button>
                  </Modal.Footer>
                </Modal>
              </React.Fragment>
            );
          })}
        </tbody>
      </Table>
    </React.Fragment>
  );
};

export default ListData;
