import React, { useEffect, useState } from "react";
import { Button } from "react-bootstrap";
import Table from "react-bootstrap/esm/Table";
import axios from "axios";

const ListData = () => {
  const [data, setData] = useState([]);
  useEffect(() => {
    axios.get("http://localhost:5000/mahasiswa").then((response) => {
      setData(response.data);
      console.log(data);
    });
  }, [response.data]);

  const deleteData = (id) => {
    axios.delete("http://localhost:5000/mahasiswa/" + id).then((response) => {
      console.log(response);
    });
  };

  return (
    <React.Fragment>
      <Table striped bordered hover size="sm">
        <thead>
          <tr>
            <th>#</th>
            <th>NIM</th>
            <th>Nama</th>
            <th style={{ width: 250 }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => {
            const i = index;
            return (
              <tr>
                <td>{index + 1}</td>
                <td>{item.nim}</td>
                <td>{item.nama}</td>
                <td>
                  <Button variant="primary" className="me-3">
                    Edit
                  </Button>
                  <Button variant="danger" onClick={deleteData(item.id)}>
                    Delete
                  </Button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </Table>
    </React.Fragment>
  );
};

export default ListData;
